mapper.select:
SELECT * FROM coupon 
WHERE userid=#{userid}
AND (usestatus != 1 OR usestatus IS NULL)

mapper.update:
    UPDATE seat
	SET reserveid = ${orderId}
	WHERE ticketid = ${ticketId}
	AND reserveid IS NULL
	LIMIT ${ticketQuantity};

    	UPDATE ticket
		SET ticketremain = ticketremain - ${tickets}
		WHERE ticketid = ${ticketId}

mapper.select:
SELECT COUNT(*)
FROM reservation
WHERE reserveid = #{orderId}
